type EdgeSVGProps = Omit<React.SVGProps<SVGGElement>, 'target'>

const Edge: React.FC<EdgeProps & EdgeSVGProps> = ({
	color,
	markerEnd,
	markerStart,
	path,
	source,
	target,
	variant = null,
	...rest
}) => {
	const namespace = `${carbonPrefix}--cc--edge`
	const pathClasses = classnames(namespace, {
		[`${namespace}--${variant}`]: variant,
		...(rest.className ? { [rest.className]: true } : {})
	})

	let d = path
	if (!d && source && target) {
		d = buildStraightPathString(source, target)
	}

	if (!d) throw Error('Missing parameters for Edge component: path or source and target.')

	return (
		<g className={pathClasses} {...rest}>
			<path d={d} className={`${namespace}__container`} />
			<path d={d} className={`${namespace}__outer`} />
			<path
				d={d}
				className={`${namespace}__inner`}
				markerEnd={`url(#${markerEnd})`}
				markerStart={`url(#${markerStart})`}
				style={{ stroke: color }}
			/>
		</g>
	)
}

export default Edge
import React, { SVGProps } from 'react'
import classnames from 'classnames'
import { carbonPrefix } from './utils'
import { arrowLeft, arrowRight, circle, diamond, square, tee } from '@carbon/charts'

interface MarkerProps extends SVGProps<SVGMarkerElement> {
	id?: string
	d?: string
	color?: string
	position?: 'start' | 'end'
	orient?: string | number
	height?: number
	width?: number
	refX?: number
	refY?: number
}

const Marker: React.FC<MarkerProps> = ({
	color,
	d,
	id,
	orient = 'auto',
	height,
	width,
	position = 'end',
	className,
	refX,
	refY,
	...rest
}) => {
	const namespace = `${carbonPrefix}--cc--marker`
	const classes = classnames(namespace, className)

	const xPos = position === 'end' ? (width || 0) / 2 + 0.5 : 0.5
	const yPos = (height || 0) / 2

	return (
		<marker
			className={classes}
			markerHeight={height}
			markerWidth={width}
			orient={orient}
			id={id}
			refX={refX || xPos}
			refY={refY || yPos}
			markerUnits="userSpaceOnUse"
			{...rest}>
			<path d={d} style={{ fill: color }} />
		</marker>
	)
}

const ArrowLeftMarker: React.FC<Omit<MarkerProps, 'd'>> = (props) => <Marker d={arrowLeft.d} {...props} />
const ArrowRightMarker: React.FC<Omit<MarkerProps, 'd'>> = (props) => <Marker d={arrowRight.d} {...props} />
const CircleMarker: React.FC<Omit<MarkerProps, 'd'>> = (props) => <Marker d={circle.d} {...props} />
const DiamondMarker: React.FC<Omit<MarkerProps, 'd'>> = (props) => <Marker d={diamond.d} {...props} />
const SquareMarker: React.FC<Omit<MarkerProps, 'd'>> = (props) => <Marker d={square.d} {...props} />
const TeeMarker: React.FC<Omit<MarkerProps, 'd'>> = (props) => <Marker d={tee.d} {...props} />

export {
	Marker,
	ArrowLeftMarker,
	ArrowRightMarker,
	CircleMarker,
	DiamondMarker,
	SquareMarker,
	TeeMarker
}
import React from 'react'
import classnames from 'classnames'
import { carbonPrefix } from './utils'

type CssPosition = 'fixed' | 'absolute' | 'relative' | 'static' | 'sticky'

type ShapeNodeProps = {
	shape?: 'circle' | 'square' | 'rounded-square'
	tag?: 'div' | 'button' | 'a'
	title?: string
	subtitle?: string
	description?: string
	renderIcon: React.ReactNode
	href?: string
	size?: string | number
	position?: CssPosition
	bodyPosition?: CssPosition
	stacked?: boolean
}

const ShapeNode: React.FC<
	ShapeNodeProps & React.HTMLAttributes<HTMLAnchorElement | HTMLDivElement | HTMLButtonElement>
> = ({
	shape = 'circle',
	tag = 'div',
	title = 'Title',
	subtitle,
	description,
	renderIcon,
	href,
	size = 48,
	stacked,
	position = 'fixed',
	bodyPosition = 'absolute',
	...rest
}) => {
	const Component = href ? 'a' : rest.onClick ? 'button' : tag

	const namespace = `${carbonPrefix}--cc--shape-node`
	const circleClasses = classnames(namespace, {
		[`${namespace}--stacked`]: stacked,
		[`${namespace}--${shape}`]: shape,
		[`${namespace}--${Component}`]: Component,
		[rest.className as string]: rest.className
	})

	const titleElement = title ? <div className={`${namespace}__title`}>{title}</div> : null
	const subtitleElement = subtitle ? (
		<div className={`${namespace}__subtitle`}>{subtitle}</div>
	) : null
	const descriptionElement = description ? (
		<div className={`${namespace}__description`}>{description}</div>
	) : null

	return (
		<Component
			className={circleClasses}
			style={{ height: size, width: size, position }}
			tabIndex={0}
			{...rest}>
			<div className={`${namespace}__icon`}>{renderIcon}</div>
			<div className={`${namespace}__body`} style={{ position: bodyPosition }}>
				{titleElement}
				{subtitleElement}
				{descriptionElement}
			</div>
		</Component>
	)
}

export default ShapeNode
