  console.log('Found an issue? https://github.com/mswjs/msw/issues')

  if (args.workerUrl) {
    console.log('Worker script URL:', args.workerUrl)
  }

  if (args.workerScope) {
    console.log('Worker scope:', args.workerScope)
  }

  console.groupEnd()
