import { NetworkError } from './utils/NetworkError'

export type MaybePromise<ValueType = any> = ValueType | Promise<ValueType>

/**
 * Internal representation of a mocked response instance.
 */
export interface MockedResponse<BodyType extends DefaultBodyType = any> {
  body: BodyType
  status: number
  statusText: string
  headers: Headers
  once: boolean
  passthrough: boolean
  delay?: number
}

export type ResponseTransformer<
  BodyType extends TransformerBodyType = any,
  TransformerBodyType extends DefaultBodyType = any,
> = (
  res: MockedResponse<TransformerBodyType>,
) => MaybePromise<MockedResponse<BodyType>>

export type ResponseFunction<BodyType extends DefaultBodyType = any> = (
  ...transformers: ResponseTransformer<BodyType>[]
) => MaybePromise<MockedResponse<BodyType>>

export type ResponseComposition<BodyType extends DefaultBodyType = any> =
  ResponseFunction<BodyType> & {
    /**
     * Respond using a given mocked response to the first captured request.
     * Does not affect any subsequent captured requests.
     */
    once: ResponseFunction<BodyType>
    networkError: (message: string) => void
  }

export const defaultResponse: Omit<MockedResponse, 'headers'> = {
  status: 200,
  statusText: 'OK',
  body: null,
  delay: 0,
  once: false,
  passthrough: false,
}

export type ResponseCompositionOptions<BodyType> = {
  defaultTransformers?: ResponseTransformer<BodyType>[]
  mockedResponseOverrides?: Partial<MockedResponse>
}

export const defaultResponseTransformers: ResponseTransformer<any>[] = []

export function createResponseComposition<BodyType extends DefaultBodyType>(
  responseOverrides?: Partial<MockedResponse<BodyType>>,
  defaultTransformers: ResponseTransformer<BodyType>[] = defaultResponseTransformers,
): ResponseFunction {
  return async (...transformers) => {
    const initialResponse: MockedResponse = Object.assign(
      {},
      defaultResponse,
      {
        headers: new Headers({
          'x-powered-by': 'msw',
        }),
      },
      responseOverrides,
    )

    const resolvedTransformers = [
      ...defaultTransformers,
      ...transformers,
    ].filter(Boolean)

    const resolvedResponse =
      resolvedTransformers.length > 0
        ? compose(...resolvedTransformers)(initialResponse)
        : initialResponse

    return resolvedResponse
  }
}

export const response = Object.assign(createResponseComposition(), {
  once: createResponseComposition({ once: true }),
  networkError(message: string) {
    throw new NetworkError(message)
  },
})
type ArityOneFunction = (arg: any) => any

type LengthOfTuple<Tuple extends any[]> = Tuple extends { length: infer L }
  ? L
  : never

type DropFirstInTuple<Tuple extends any[]> = ((...args: Tuple) => any) extends (
  arg: any,
  ...rest: infer LastArg
) => any
  ? LastArg
  : Tuple

type LastInTuple<Tuple extends any[]> = Tuple[LengthOfTuple<
  DropFirstInTuple<Tuple>
>]

type FirstFnParameterType<Functions extends ArityOneFunction[]> = Parameters<
  LastInTuple<Functions>
>[any]

type LastFnParameterType<Functions extends ArityOneFunction[]> = ReturnType<
  Functions[0]
>

/**
 * Composes a given list of functions into a new function that
 * executes from right to left.
 */
export function compose<
  Functions extends ArityOneFunction[],
  LeftReturnType extends FirstFnParameterType<Functions>,
  RightReturnType extends LastFnParameterType<Functions>,
>(
  ...fns: Functions
): (
  ...args: [LeftReturnType] extends [never] ? never[] : [LeftReturnType]
) => RightReturnType {
  return (...args) => {
    return fns.reduceRight((leftFn: any, rightFn) => {
      return leftFn instanceof Promise
        ? Promise.resolve(leftFn).then(rightFn)
        : rightFn(leftFn)
    }, args[0])
  }
}
// Ignore the source files traces for local testing.
const SOURCE_FRAME = /[\/\\]msw[\/\\]src[\/\\](.+)/

const BUILD_FRAME =
  /(node_modules)?[\/\\]lib[\/\\](umd|esm|iief|cjs)[\/\\]|^[^\/\\]*$/

/**
 * Return the stack trace frame of a function's invocation.
 */
export function getCallFrame(error: Error) {
  // In <IE11, new Error may return an undefined stack
  const stack = error.stack

  if (!stack) {
    return
  }

  const frames: string[] = stack.split('\n').slice(1)

  // Get the first frame that doesn't reference the library's internal trace.
  // Assume that frame is the invocation frame.
  const declarationFrame = frames.find((frame) => {
    return !(SOURCE_FRAME.test(frame) || BUILD_FRAME.test(frame))
  })

  if (!declarationFrame) {
    return
  }

  // Extract file reference from the stack frame.
  const declarationPath = declarationFrame
    .replace(/\s*at [^()]*\(([^)]+)\)/, '$1')
    .replace(/^@/, '')
  return declarationPath
}
/**
 * Determines if the given function is an iterator.
 */
export function isIterable<IteratorType>(
  fn: any,
): fn is Generator<IteratorType, IteratorType, IteratorType> {
  if (!fn) {
    return false
  }

  return typeof (fn as Generator<unknown>)[Symbol.iterator] == 'function'
}
