export type ParsedRestRequest = Match

export class RestRequest<
  RequestBody extends DefaultBodyType = DefaultBodyType,
  RequestParams extends PathParams = PathParams,
> extends MockedRequest<RequestBody> {
  constructor(
    request: MockedRequest<RequestBody>,
    public readonly params: RequestParams,
  ) {
    super(request.url, {
      ...request,
      /**
       * @deprecated https://github.com/mswjs/msw/issues/1318
       * @note Use internal request body buffer as the body init
       * because "request.body" is a getter that will trigger
       * request body parsing at this step.
       */
      body: request['_body'],
    })
    this.id = request.id
  }
}

/**
 * Request handler for REST API requests.
 * Provides request matching based on method and URL.
 */
export class RestHandler<
  RequestType extends MockedRequest<DefaultBodyType> = MockedRequest<DefaultBodyType>,
> extends RequestHandler<
  RestHandlerInfo,
  RequestType,
  ParsedRestRequest,
  RestRequest<
    RequestType extends MockedRequest<infer RequestBodyType>
      ? RequestBodyType
      : any,
    PathParams
  >
> {
  constructor(
    method: RestHandlerMethod,
    path: Path,
    resolver: ResponseResolver<any, any>,
  ) {
    super({
      info: {
        header: `${method} ${path}`,
        path,
        method,
      },
      ctx: restContext,
      resolver,
    })

    this.checkRedundantQueryParameters()
  }

  private checkRedundantQueryParameters() {
    const { method, path } = this.info

    if (path instanceof RegExp) {
      return
    }

    const url = cleanUrl(path)

    // Bypass request handler URLs that have no redundant characters.
    if (url === path) {
      return
    }

    const searchParams = getSearchParams(path)
    const queryParams: string[] = []

    searchParams.forEach((_, paramName) => {
      queryParams.push(paramName)
    })

    devUtils.warn(
      `Found a redundant usage of query parameters in the request handler URL for "${method} ${path}". Please match against a path instead and access query parameters in the response resolver function using "req.url.searchParams".`,
    )
  }

  parse(request: RequestType, resolutionContext?: ResponseResolutionContext) {
    return matchRequestUrl(
      request.url,
      this.info.path,
      resolutionContext?.baseUrl,
    )
  }

  protected getPublicRequest(
    request: RequestType,
    parsedResult: ParsedRestRequest,
  ): RestRequest<any, PathParams> {
    return new RestRequest(request, parsedResult.params || {})
  }

  predicate(request: RequestType, parsedResult: ParsedRestRequest) {
    const matchesMethod =
      this.info.method instanceof RegExp
        ? this.info.method.test(request.method)
        : isStringEqual(this.info.method, request.method)

    return matchesMethod && parsedResult.matches
  }

  log(request: RequestType, response: SerializedResponse<any>) {
    const publicUrl = getPublicUrlFromRequest(request)
    const loggedRequest = prepareRequest(request)
    const loggedResponse = prepareResponse(response)
    const statusColor = getStatusCodeColor(response.status)

    console.groupCollapsed(
      devUtils.formatMessage('%s %s %s (%c%s%c)'),
      getTimestamp(),
      request.method,
      publicUrl,
      `color:${statusColor}`,
      `${response.status} ${response.statusText}`,
      'color:inherit',
    )
    console.log('Request', loggedRequest)
    console.log('Handler:', this)
    console.log('Response', loggedResponse)
    console.groupEnd()
  }
}
import { invariant } from 'outvariant'
import { ResponseTransformer } from '../response'
import { devUtils } from '../utils/internal/devUtils'
import { jsonParse } from '../utils/internal/jsonParse'
import { mergeRight } from '../utils/internal/mergeRight'
import { json } from './json'

type ForbiddenFieldNames = '' | 'data' | 'errors' | 'extensions'

/**
 * Set a custom field on the GraphQL mocked response.
 * @example res(ctx.fields('customField', value))
 * @see {@link https://mswjs.io/docs/api/context/field}
 */
export const field = <FieldNameType extends string, FieldValueType>(
  fieldName: FieldNameType extends ForbiddenFieldNames ? never : FieldNameType,
  fieldValue: FieldValueType,
): ResponseTransformer<string> => {
  return (res) => {
    validateFieldName(fieldName)

    const prevBody = jsonParse(res.body) || {}
    const nextBody = mergeRight(prevBody, { [fieldName]: fieldValue })

    return json(nextBody)(res as any) as any
  }
}

function validateFieldName(fieldName: string) {
  invariant(
    fieldName.trim() !== '',
    devUtils.formatMessage(
      'Failed to set a custom field on a GraphQL response: field name cannot be empty.',
    ),
  )

  invariant(
    fieldName !== 'data',
    devUtils.formatMessage(
      'Failed to set a custom "%s" field on a mocked GraphQL response: forbidden field name. Did you mean to call "ctx.data()" instead?',
      fieldName,
    ),
  )

  invariant(
    fieldName !== 'errors',
    devUtils.formatMessage(
      'Failed to set a custom "%s" field on a mocked GraphQL response: forbidden field name. Did you mean to call "ctx.errors()" instead?',
      fieldName,
    ),
  )

  invariant(
    fieldName !== 'extensions',
    devUtils.formatMessage(
      'Failed to set a custom "%s" field on a mocked GraphQL response: forbidden field name. Did you mean to call "ctx.extensions()" instead?',
      fieldName,
    ),
  )
}
export function tryCatch<Fn extends (...args: any[]) => any>(
  fn: Fn,
  onException?: (error: Error) => void,
): ReturnType<Fn> | undefined {
  try {
    const result = fn()
    return result
  } catch (error) {
    onException?.(error as Error)
  }
}
